package com.avmy.palindrome

import com.avmy.palindrome.PalindromicSubstrings.countSubstrings
import org.junit.jupiter.api.Test

import org.junit.jupiter.api.Assertions.*
import org.junit.jupiter.params.ParameterizedTest
import org.junit.jupiter.params.provider.Arguments
import org.junit.jupiter.params.provider.MethodSource
import java.util.stream.Stream


internal class PalindromicSubstringsTest {

    companion object {
        @JvmStatic
        fun stringPair(): Stream<Arguments> {
            return Stream.of(
                Arguments.of(Pair("abc", 3)),
                Arguments.of(Pair("aaa", 6)),
                Arguments.of(Pair("aaaccc", 12)),
                Arguments.of(Pair("aaafccc", 13)),
                Arguments.of(Pair("aaaffccc", 15)),
                Arguments.of(Pair("", 1028)),
            )
        }
    }

    @ParameterizedTest
    @MethodSource(value = ["stringPair"])
    fun countSubstringsTest(data: Pair<String, Int>) {
        val result = countSubstrings(data.first)

        kotlin.test.assertEquals(data.second, result)
    }
}