package com.avmy.str

import org.junit.jupiter.api.Assertions.assertEquals
import org.junit.jupiter.params.ParameterizedTest
import org.junit.jupiter.params.provider.Arguments
import org.junit.jupiter.params.provider.MethodSource
import java.util.stream.Stream


class LongestHappyPrefixTest {


    companion object {
        @JvmStatic
        fun pair(): Stream<Arguments> {
            return Stream.of(
                    Arguments.of(Pair("ababab", "abab")),
                    Arguments.of(Pair("level", "l")),
                    Arguments.of(Pair("", "")),
            )
        }
    }



    @ParameterizedTest
    @MethodSource(value = ["pair"])
    fun longestPrefixTest(data: Pair<String, String>) {
        val prefix = LongestHappyPrefix.longestPrefix(data.first)
        assertEquals(data.second, prefix)
    }
}