package com.avmy.str

import org.junit.jupiter.api.Assertions
import org.junit.jupiter.params.ParameterizedTest
import org.junit.jupiter.params.provider.Arguments
import org.junit.jupiter.params.provider.MethodSource
import java.util.stream.Stream

internal class SumScoresBuiltStringsTest {

    companion object {
        @JvmStatic
        fun pair(): Stream<Arguments> {
            return Stream.of(
                    Arguments.of(Pair("azbazbazbzaz", 23L)),
                    Arguments.of(Pair("azbazbzaz", 14L)),
                    Arguments.of(Pair("azbazbz", 10L)),
                    Arguments.of(Pair("babab", 9L)),
                    Arguments.of(Pair("bbababb", 12L)),
                    Arguments.of(Pair("bacabacab", 15L)),
                    Arguments.of(Pair("", 982L)),
                    Arguments.of(Pair("", 3979L)),
                    Arguments.of(Pair("", 6661L)),
            )
        }
    }


    @ParameterizedTest
    @MethodSource(value = ["pair"])
    fun deleteStringTest(data: Pair<String, Int>) {
        val result = SumScoresBuiltStrings.sumScores(data.first)

        Assertions.assertEquals(data.second, result)
    }

}